import { useEffect, useState } from 'react';

function App() {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    fetch('http://localhost:5000/emails')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setEmails(data);
          setAuthenticated(true);
        } else {
          setAuthenticated(false);
        }
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching emails:", err);
        setAuthenticated(false);
        setLoading(false);
      });
  }, []);

  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/authorize';
  };

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>ðŸ“¬ MailGlance - Email Summaries</h1>

      {!authenticated ? (
        <>
          <p>Please log in to view your Gmail summaries.</p>
          <button onClick={handleLogin} style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: "#4285F4",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer"
          }}>
            Sign in with Google
          </button>
        </>
      ) : loading ? (
        <p>Loading emails...</p>
      ) : (
        emails.map((email, index) => (
          <div key={index} style={{ border: "1px solid #ccc", marginBottom: "10px", padding: "10px", borderRadius: "8px" }}>
            <h3>ðŸ“§ {email.subject}</h3>
            <p><strong>From:</strong> {email.from}</p>
            <p>{email.body}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default App;
