import React, { useEffect, useState } from "react";

function Emails() {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/emails", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => setEmails(data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">ðŸ“§ Your Summarized Emails</h1>
      {emails.length === 0 ? (
        <p>Loading or no emails found...</p>
      ) : (
        emails.map((email, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow-md p-5 mb-4 border-l-4 border-blue-500"
          >
            <h2 className="text-xl font-semibold">{email.subject}</h2>
            <p className="text-gray-600 text-sm mb-2">{email.from}</p>
            <p className="mb-3">{email.body}</p>
            <a
              href={email.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 hover:underline"
            >
              Read full email â†’
            </a>
          </div>
        ))
      )}
    </div>
  );
}

export default Emails;
