import React, { useEffect, useState } from "react";

const Emails = () => {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/emails", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        console.log("API response:", data);
        setEmails(data);
      })
      .catch((err) => console.error("Error fetching emails:", err));
  }, []);

  return (
    <div className="App">
      <h2 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "1.5rem", textAlign: "center" }}>
        Your Summarized Emails
      </h2>
      {emails.length === 0 ? (
        <p style={{ textAlign: "center", color: "#666" }}>No emails found.</p>
      ) : (
        <div className="email-list">
          {emails.map((email) => (
            <div key={email.id} className="email-card">
              <h3>{email.subject || "No Subject"}</h3>
              <p><strong>From:</strong> {email.from}</p>
              <p>{email.summary}</p>
              <a
                href={`https://mail.google.com/mail/u/0/#inbox/${email.id}`}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  display: "inline-block",
                  marginTop: "1rem",
                  color: "#007bff",
                  textDecoration: "none",
                  fontWeight: "bold",
                }}
              >
                Open in Gmail â†’
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Emails;
