import React, { useEffect, useState } from "react";

function Emails() {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/emails", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => setEmails(data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="px-6 py-8 bg-gradient-to-br from-gray-100 via-white to-gray-200 min-h-screen">
      <h1 className="text-4xl font-extrabold text-blue-800 mb-6 drop-shadow-sm">ðŸ“§ Your Summarized Emails</h1>
      {emails.length === 0 ? (
        <div className="text-gray-600 text-lg animate-pulse">Loading or no emails found...</div>
      ) : (
        <div className="space-y-6">
          {emails.map((email, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl shadow-md p-6 border-l-8 border-blue-400 hover:shadow-xl transition duration-300"
            >
              <h2 className="text-xl font-bold text-gray-900 mb-1">{email.subject || "(No Subject)"}</h2>
              <p className="text-sm text-gray-500 mb-2">{email.from}</p>
              <p className="text-gray-700 mb-4 whitespace-pre-line">{email.summary}</p>
              {email.link && (
                <a
                  href={email.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 font-medium hover:underline"
                >
                  Read full email â†’
                </a>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Emails;
