import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchEmails = () => {
    setLoading(true);
    fetch('http://localhost:5000/emails')
      .then(res => res.json())
      .then(data => {
        setEmails(data);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchEmails();
  }, []);

  return (
    <div className="App">
      <h1>ðŸ“¬ MailGlance - Summarized Emails</h1>

      <button className="refresh-btn" onClick={fetchEmails} disabled={loading}>
        {loading ? "Refreshing..." : "ðŸ”„ Refresh"}
      </button>

      <div className="email-list">
        {emails.map((email, index) => (
          <div key={index} className="email-card">
            <h3>ðŸ“§ {email.subject}</h3>
            <p><strong>From:</strong> {email.from}</p>
            <p><strong>Summary:</strong> {email.summary}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
