import React from "react";

function Home() {
  const handleLogin = () => {
    window.location.href = "http://localhost:5000/authorize";
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-4xl font-bold mb-6">ðŸ“¬ MailGlance</h1>
      <p className="mb-4 text-lg">Summarize your Gmail in a glance</p>
      <button
        onClick={handleLogin}
        className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-3 rounded-lg shadow-md transition duration-300"
      >
        Sign in with Google
      </button>
    </div>
  );
}

export default Home;
