import React from "react";

const Home = () => {
  const handleLogin = () => {
    window.location.href = "http://localhost:5000/authorize";
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-purple-200">
      <h1 className="text-4xl font-bold text-gray-800 mb-4">ðŸ“¬ MailGlance</h1>
      <p className="text-lg text-gray-600 mb-8">Summarize your emails with one click.</p>
      <button
        onClick={handleLogin}
        className="bg-red-500 hover:bg-red-600 text-white font-semibold px-6 py-3 rounded-lg shadow-md transition"
      >
        Sign in with Google
      </button>
    </div>
  );
};

export default Home;
