      .then(res => res.json())
      .then(data => setEmails(data));
  }, []);

  return (
    <div className="App">
      <h1>ðŸ“¬ MailGlance - Summarized Emails</h1>
      <div className="email-list">
        {emails.map((email, index) => (
          <div key={index} className="email-card">
            <h3>ðŸ“§ {email.subject}</h3>
            <p><strong>From:</strong> {email.from}</p>
            <p><strong>Summary:</strong> {email.body}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
