import React from "react";

function Home() {
  const handleLogin = () => {
    window.location.href = "http://localhost:5000/authorize";
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-tr from-blue-100 via-white to-blue-200 text-center">
      <h1 className="text-5xl font-extrabold text-blue-700 mb-6 drop-shadow-md">ðŸ“¬ MailGlance</h1>
      <p className="mb-4 text-lg text-gray-700 max-w-md">Instantly summarize and read through your Gmail in a beautiful, streamlined interface.</p>
      <button
        onClick={handleLogin}
        className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-8 py-3 rounded-full shadow-lg transform hover:scale-105 transition-all duration-300"
      >
        Sign in with Google
      </button>
    </div>
  );
}

export default Home;
