import React, { useEffect, useState } from "react";
import "../App.css";

const Emails = () => {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/emails", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        console.log("API response:", typeof data, data);
        setEmails(data);
      })
      .catch((err) => console.error("Error fetching emails:", err));
  }, []);

  return (
    <div className="App">
      <div className="controls-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Search emails by subject, sender, or keyword..."
          // Add onChange logic later
        />

        <div className="filter-settings">
          <select className="filter-dropdown">
            <option value="">Filter by label</option>
            <option value="inbox">Inbox</option>
            <option value="starred">Starred</option>
            <option value="sent">Sent</option>
          </select>

          <select className="filter-dropdown">
            <option value="">Filter by sender</option>
            <option value="example@gmail.com">example@gmail.com</option>
            {/* Dynamically generate later */}
          </select>

          <select className="filter-dropdown">
            <option value="">Filter by date</option>
            <option value="24h">Last 24 Hours</option>
            <option value="week">Last Week</option>
            <option value="month">Last Month</option>
          </select>

          <select className="filter-dropdown">
            <option value="">Filter by keyword</option>
            <option value="invoice">Invoice</option>
            <option value="meeting">Meeting</option>
            <option value="project">Project</option>
          </select>

          <select className="settings-dropdown">
            <option value="short">Summary: Short (~50 words)</option>
            <option value="medium">Summary: Medium (~100 words)</option>
            <option value="full">Summary: Full (~200 words)</option>
          </select>
        </div>
      </div>
      <h2 className="email-heading">Your Summarized Emails</h2>
      {emails.length === 0 ? (
        <p className="email-empty">No emails found.</p>
      ) : (
        <div className="email-list">
          {emails.map((email, index) => (
            <div key={email.id}>
              <div className="email-card">
                <h3>{email.subject || "No Subject"}</h3>
                <p><strong>From:</strong> {email.from}</p>
                <p>{email.summary}</p>
                <a
                  href={`https://mail.google.com/mail/u/0/#inbox/${email.id}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    display: "inline-block",
                    marginTop: "1rem",
                    color: "#007bff",
                    textDecoration: "none",
                    fontWeight: "bold",
                  }}
                >
                  Open in Gmail →
                </a>
              </div>
              {index !== emails.length - 1 && <hr className="email-divider" />}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Emails;
