import { useEffect, useState } from 'react';

function App() {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/emails')
      .then(res => res.json())
      .then(data => {
        if (data.message) {
          console.log(data.message); // fallback if emails aren't returned
        } else {
          setEmails(data);
        }
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching emails:", err);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>ðŸ“¬ MailGlance - Email Summaries</h1>
      {loading ? (
        <p>Loading emails...</p>
      ) : emails.length > 0 ? (
        emails.map((email, index) => (
          <div key={index} style={{ border: "1px solid #ccc", marginBottom: "10px", padding: "10px", borderRadius: "8px" }}>
            <h3>ðŸ“§ {email.subject}</h3>
            <p><strong>From:</strong> {email.from}</p>
            <p>{email.body}</p>
          </div>
        ))
      ) : (
        <p>No emails found.</p>
      )}
    </div>
  );
}

export default App;
